class Solution:
    def maximumSetSize(self, nums1: List[int], nums2: List[int]) -> int:
        count1, count2 = {}, {}
        for num in nums1:
            count1[num] = count1.get(num, 0) + 1
        for num in nums2:
            count2[num] = count2.get(num, 0) + 1

        removed1, removed2 = 0, 0
        for num in count1:
            removed1 += min(count1[num], len(nums1) // 2)
        for num in count2:
            removed2 += min(count2[num], len(nums2) // 2)

        remaining1, remaining2 = len(nums1) - removed1, len(nums2) - removed2
        return len(set(list(nums1)[:remaining1] + list(nums2)[:remaining2]))
