class Solution:
    def maximumSetSize(self, nums1: List[int], nums2: List[int]) -> int:
        from collections import Counter
        count1, count2 = Counter(nums1), Counter(nums2)
        common = count1 & count2
        for k in sorted(common, key=common.get, reverse=True):
            del count1[k]
            del count2[k]
        return len(set(list(count1.keys()) + list(count2.keys())))
