class Solution:
    def maximumSetSize(self, nums1: List[int], nums2: List[int]) -> int:
        count1 = {}
        count2 = {}

        for num in nums1:
            if num in count1:
                count1[num] += 1
            else:
                count1[num] = 1

        for num in nums2:
            if num in count2:
                count2[num] += 1
            else:
                count2[num] = 1

        nums1.sort(key=lambda x: count1[x], reverse=True)
        nums2.sort(key=lambda x: count2[x], reverse=True)

        s = set()
        i, j = 0, 0
        while len(s) < len(set(nums1 + nums2)):
            if i < len(nums1) and (j == len(nums2) or count1[nums1[i]] >= count2[nums2[j]]):
                s.add(nums1[i])
                i += 1
            else:
                s.add(nums2[j])
                j += 1

        return len(s)
