    def maxPartitionsAfterOperations(self, s: str, k: int) -> int:
        def count_distinct_chars(substring):
            return len(set(substring))

        def max_partitions(s, k):
            max_partitions = 0
            for i in range(len(s)):
                changed_s = s[:i] + chr(ord('a') + (ord(s[i]) - ord('a') + 1) % 26) + s[i+1:]
