class Solution:
    def minimumOperationsToMakeEqual(self, x: int, y: int) -> int:
        if x > y:
            x, y = y, x
        res = 0
        while x < y:
            if y % 11 == 0:
                y //= 11
            elif y % 5 == 0:
                y //= 5
            else:
                y -= 1
            res += 1
        return res
