class Solution:
    def findMaximumNumber(self, k: int, x: int) -> int:
        def count_set_bits(n, x):
            count = 0
            i = x - 1
            while i < len(bin(n)[2:]):
                if bin(n)[2:][-(i + 1)] == '1':
                    count += 1
                i += x
            return count

        total_price = 0
        num = 0
        while total_price <= k:
            num += 1
            total_price += count_set_bits(num, x)

        return num - 1
