class Solution:
    def minimumArrayLength(self, nums: List[int]) -> int:
        nums.sort(reverse=True)
        for i in range(len(nums)):
            if nums[i] == 1:
                return 1
            for j in range(i + 1, len(nums)):
                if nums[i] % nums[j] != 0:
                    nums.append(nums[i] % nums[j])
                    nums[i] = nums[j] = 0
        return len([x for x in nums if x > 0])
```

