class Solution:
    def minOrAfterOperations(self, nums: List[int], k: int) -> int:
        n = len(nums)
        res = nums[:]
        for _ in range(k):
            temp = [res[0]]
            for i in range(1, n - 1):
                temp.append(res[i] & res[i - 1])
            temp.append(res[-1])
            res = temp
        or_val = 0
        for num in res:
            or_val |= num
        return or_val
```

