class Solution:
    def maxSelectedElements(self, nums: List[int]) -> int:
        n = len(nums)
        count = [0] * (max(nums) + 2)
        for num in nums:
            count[num] += 1

        dp = [0] * (max(nums) + 2)
        for i in range(1, max(nums) + 2):
            dp[i] = max(dp[i-1], dp[i-2] + count[i])

        return dp[-1]
```
