class Solution:
    def maxSelectedElements(self, nums: List[int]) -> int:
        n = len(nums)
        count = [0] * (10**6 + 1)
        for num in nums:
            count[num] += 1

        res = 0
        left, right = 0, 0
        while right < 10**6 + 1:
            if count[right] > 0:
                count[right] -= 1
                right += 1
                res = max(res, right - left)
            else:
                left += 1
                if left <= right:
                    res = max(res, right - left)

