class Solution:
    def maximumStrength(self, nums: List[int], k: int) -> int:
        n = len(nums)
        prefix_sum = [0] * (n + 1)
        for i in range(n):
            prefix_sum[i + 1] = prefix_sum[i] + nums[i]

        dp = [[0] * (k + 1) for _ in range(n + 1)]
        for i in range(1, n + 1):
            for j in range(1, min(i, k) + 1):
                for x in range(j - 1, i):
                    dp[i][j] = max(dp[i][j], dp[x][j - 1] + (prefix_sum[i] - prefix_sum[x]) * (j - (i - x)))
                    if j % 2 == 1:
                        dp[i][j] -= (prefix_sum[i] - prefix_sum[x]) * (j - (i - x) - 1)
                    else:
                        dp[i][j] += (prefix_sum[i] - prefix_sum[x]) * (j - (i - x) - 1)

        return dp[n][k]
