class Solution:
    def sumOfPowers(self, nums: List[int], k: int) -> int:
        MOD = 10**9 + 7
        n = len(nums)
        nums.sort()
        dp = [[0]*(k+1) for _ in range(n+1)]

        for i in range(1, n+1):
            dp[i][1] = 0

        for j in range(2, k+1):
            for i in range(j, n+1):
                dp[i][j] = dp[i-1][j-1] + min((nums[i-1] - nums[i-j]) % MOD, (nums[i-j] - nums[i-1]) % MOD)
                dp[i][j] %= MOD

        return sum(dp[i][k] for i in range(k, n+1)) % MOD
