class Solution:
    def maxPalindromesAfterOperations(self, words: List[str]) -> int:
        count = [{} for _ in range(26)]
        for word in words:
            for char in word:
                count[ord(char) - ord('a')][word] = count[ord(char) - ord('a')].get(word, 0) + 1
        res = 0
        for i in range(26):
            freq = list(count[i].values())
            freq.sort(reverse=True)
            for j in range(0, len(freq), 2):
                res += 1
        return res
