        for word in words:
            for char in set(word):
                count[ord(char) - ord('a')][word.count(char)] = count[ord(char) - ord('a')].get(word.count(char), 0) + 1
        res = 0
        for i in range(26):
            for j in range(26, -1, -1):
                if j >= 2:
                    res += count[i].get(j, 0)
                else:
                    res += count[i].get(j, 0) // 2 * 2
                    res += count[i].get(j, 0) % 2
