class Solution:
    def maxOperations(self, nums: List[int]) -> int:
        count = defaultdict(int)
        for num in nums:
            count[num] += 1
        nums.sort()
        res = 0
        i, j = 0, len(nums) - 1
        while i < j:
            if nums[i] + nums[j] == nums[i + 1] + nums[j - 1]:
                res += 1
                i += 1
                j -= 1
            elif nums[i] + nums[j] < nums[i + 1] + nums[j - 1]:
                j -= 1
            else:
                i += 1
        return res
