        for i in range(n):
            if possible[i] == 1:
                alice_points += 1
            else:
                alice_points -= 1
            if i > 0:
                if possible[n - i] == 1:
                    bob_points += 1
                else:
                    bob_points -= 1
            if alice_points > bob_points:
                return i + 1
        return -1
