class Solution:
    def minimumDeletions(self, word: str, k: int) -> int:
        freq = {}
        for char in word:
            if char in freq:
                freq[char] += 1
            else:
                freq[char] = 1

        freq_values = sorted(freq.values())
        n = len(freq_values)

        res = float('inf')
        for i in range(n):
            max_freq = freq_values[i]
            min_freq = max_freq - k
            j = n - 1
            while j >= 0 and freq_values[j] > min_freq:
                j -= 1
            res = min(res, len(word) - (i + 1) - (n - j - 1))

        return res
