class Solution:
    def mostFrequentIDs(self, nums: List[int], freq: List[int]) -> List[int]:
        freq_count = {}
        max_freq = 0
        ans = []

        for i in range(len(nums)):
            freq_count[nums[i]] = freq_count.get(nums[i], 0) + freq[i]
