class Solution:
    def stringIndices(self, wordsContainer: List[str], wordsQuery: List[str]) -> List[int]:
        def lcs(s1, s2):
            m, n = len(s1), len(s2)
            dp = [[0] * (n + 1) for _ in range(m + 1)]
            for i in range(m + 1):
                for j in range(n + 1):
                    if i == 0 or j == 0:
                        dp[i][j] = 0
                    elif s1[i - 1] == s2[j - 1]:
                        dp[i][j] = dp[i - 1][j - 1] + 1
                    else:
                        dp[i][j] = max(dp[i - 1][j], dp[i][j - 1])
            return dp[m][n]

        res = []
        for query in wordsQuery:
            max_lcs, idx = 0, -1
            for i, container in enumerate(wordsContainer):
                lcs_len = lcs(query, container)
                if lcs_len > max_lcs or (lcs_len == max_lcs and len(container) < len(wordsContainer[idx])):
                    max_lcs = lcs_len
                    idx = i
            res.append(idx)
        return res
```

