            i = min(len(s1), len(s2)) - 1
            while i >= 0 and s1[i] == s2[i]:
                i -= 1
            return min(len(s1), len(s2)) - i - 1

        ans = []
        for query in wordsQuery:
            max_suffix_len = 0
            min_len = float('inf')
