            for i, container in enumerate(wordsContainer):
                suffix_len = common_suffix_len(query, container)
                if suffix_len > max_suffix_len or (suffix_len == max_suffix_len and len(container) < min_len):
                    max_suffix_len = suffix_len
                    min_len = len(container)
                    idx = i
            ans.append(idx)
        return ans
