class Solution:
    def stringIndices(self, wordsContainer: List[str], wordsQuery: List[str]) -> List[int]:
        def longest_common_suffix(s1, s2):
            i = len(s1) - 1
            j = len(s2) - 1
            while i >= 0 and j >= 0:
                if s1[i] != s2[j]:
                    break
                i -= 1
                j -= 1
            return len(s1) - i - 1

        ans = []
        for query in wordsQuery:
            max_len = 0
            min_len = float('inf')
            idx = -1
            for i, container in enumerate(wordsContainer):
                len_suffix = longest_common_suffix(query, container)
                if len_suffix > max_len:
                    max_len = len_suffix
                    min_len = len(container)
                    idx = i
                elif len_suffix == max_len:
                    if len(container) < min_len:
                        min_len = len(container)
                        idx = i
            ans.append(idx)
        return ans
```

