class Solution:
    def minOperationsToMakeMedianK(self, nums: List[int], k: int) -> int:
        nums.sort()
        n = len(nums)
        res = float('inf')

        for i in range(n):
            left, right = i, n - i - 1
            ops = 0
            for j in range(n):
                if j <= i:
                    ops += abs(nums[j] - k)
                else:
                    ops += abs(nums[j] - k) - (nums[j] - nums[i])
            res = min(res, ops)

        return res
