class Solution:
    def minOperationsToMakeMedianK(self, nums: List[int], k: int) -> int:
        nums.sort()
        n = len(nums)
        res = float('inf')

        for i in range(n):
            left, right = i, n - 1
            ops = 0

            while left < right:
                if nums[left] + nums[right] < 2 * k:
                    ops += k - nums[left]
                    left += 1
                elif nums[left] + nums[right] > 2 * k:
                    ops += nums[right] - k
                    right -= 1
                else:
                    left += 1
                    right -= 1

            if left == right:
                ops += abs(nums[left] - k)

            res = min(res, ops)

        return res
