class Solution:
    def minimumSubstringsInPartition(self, s: str) -> int:
        count = 0
        char_count = {}

        for char in s:
            if char in char_count:
                char_count[char] += 1
            else:
                char_count[char] = 1

        while char_count:
            temp_count = {}
            temp_str = ""
            for char in s:
                if char in temp_count:
                    temp_count[char] += 1
                else:
                    temp_count[char] = 1
                temp_str += char
                if all(val == temp_count[char] for val in temp_count.values()):
                    s = s.replace(temp_str, "", 1)
                    char_count = {k: v - temp_count[k] for k, v in char_count.items() if v - temp_count[k] > 0}
                    count += 1
                    break

        return count
```

