class Solution:
    def minimumSubstringsInPartition(self, s: str) -> int:
        def is_balanced(substring):
            char_count = {}
            for char in substring:
                if char in char_count:
                    char_count[char] += 1
                else:
                    char_count[char] = 1
            return len(set(char_count.values())) == 1

        partitions = 0
        i = 0
        while i < len(s):
            for j in range(len(s), i, -1):
                if is_balanced(s[i:j]):
                    partitions += 1
                    i = j
                    break
        return partitions
```

