class Solution:
    def findProductsOfElements(self, queries: List[List[int]]) -> List[int]:
        MOD = 10**9 + 7
        N = 10**6 + 1
        pw2 = [1]
        for _ in range(N - 1):
            pw2.append((pw2[-1] * 2) % MOD)
        inv_pw2 = [pow(pw2[i], -1, MOD) for i in range(N)]

        def get_powerful_array(x):
            pw_array = []
            i = 0
            while x > 0:
                while pw2[i] > x:
                    i -= 1
                pw_array.append(pw2[i])
                x -= pw2[i]
                i += 1
            return pw_array

        big_nums = [1]
        for i in range(1, N):
            big_nums.extend(get_powerful_array(i))

        res = []
        for from_i, to_i, mod_i in queries:
            prod = 1
            for i in range(from_i, to_i + 1):
                for num in get_powerful_array(i):
                    prod = (prod * num) % mod_i
            res.append(prod)
        return res
```

