class Solution:
    def findProductsOfElements(self, queries: List[List[int]]) -> List[int]:
        MOD = 10**9 + 7
        N = 10**6 + 1
        pw2 = [1]
        for _ in range(N - 1):
            pw2.append(pw2[-1] * 2 % MOD)
        inv_pw2 = [pow(pw2[-1], MOD - 2, MOD)]
        for _ in range(N - 1, 0, -1):
            inv_pw2.append(inv_pw2[-1] * pw2[_ - 1] % MOD)
        inv_pw2 = inv_pw2[::-1]

        big_nums = [1]
        pw2_sum = 1
        for i in range(1, N):
            if pw2_sum * 2 <= i:
                pw2_sum *= 2
                big_nums.append(pw2_sum)
            else:
                big_nums.append(big_nums[-1])

        prefix_product = [1]
        for num in big_nums:
            prefix_product.append(prefix_product[-1] * num % MOD)

        result = []
        for from_i, to_i, mod_i in queries:
            product = prefix_product[to_i + 1] * inv_pw2[from_i] % MOD
            result.append(product % mod_i)

        return result
```

