class Solution:
    def findWinningPlayer(self, skills: List[int], k: int) -> int:
        n = len(skills)
        queue = list(range(n))
        skill_dict = {skill: i for i, skill in enumerate(skills)}
        win_count = 0
        winner = -1

        while True:
            player1 = queue.pop(0)
            player2 = queue.pop(0)
            if skills[player1] > skills[player2]:
                winner = player1
                queue.append(player2)
            else:
                winner = player2
                queue.append(player1)
            win_count += 1
            if win_count == k:
                return winner
            if queue[0] == winner:
                win_count -= 1
