            peaks = 0
            for i in range(left + 1, right):
                if nums[i] > nums[i - 1] and nums[i] > nums[i + 1]:
                    peaks += 1
            return peaks

        answer = []
        for query in queries:
            if query[0] == 1:
                left, right = query[1], query[2]
                answer.append(count_peaks(left, right))
            else:
                index, val = query[1], query[2]
                nums[index] = val
        return answer
