        MOD = 10**9 + 7
        dp = [[0]*(401) for _ in range(n+1)]
        dp[0][0] = 1
        for i in range(n):
            for j in range(400, -1, -1):
                for k in range(i, -1, -1):
                    dp[i+1][j+k] += dp[i][j]
                    dp[i+1][j+k] %= MOD
        res = 0
        for req in requirements:
            res += dp[req[0]+1][req[1]]
            res %= MOD
        return res
