from functools import lru_cache
from math import comb
from typing import List

class Solution:
    def numberOfPermutations(self, n: int, requirements: List[List[int]]) -> int:
        MOD = 10**9 + 7
        dp = [{} for _ in range(n + 1)]

        @lru_cache(None)
        def count_inversions(i, j, k):
            if k < 0:
                return 0
            if i == 0:
                return 1
            res = 0
            for x in range(i):
                res += count_inversions(x, j - 1, k - (i - x - 1))
                res %= MOD
            return res

        @lru_cache(None)
        def dfs(i, j, k):
            if i == n:
                return 1
            if (i, j, k) in dp[i]:
                return dp[i][(i, j, k)]
            res = 0
            for x in range(i):
                if k >= i - x - 1:
                    res += dfs(i + 1, j + (x < j), k - (i - x - 1))
                    res %= MOD
            dp[i][(i, j, k)] = res
            return res

        res = 0
        for perm in range(n):
            cnt = 0
            for req in requirements:
                if req[0] >= perm:
                    cnt += count_inversions(perm, req[0], req[1])
                    cnt %= MOD
            res += dfs(perm, perm, cnt)
            res %= MOD
        return res
```

