from functools import lru_cache
from math import comb
from typing import List

class Solution:
    def numberOfPermutations(self, n: int, requirements: List[List[int]]) -> int:
        MOD = 10**9 + 7
        @lru_cache(None)
        def dp(mask, inversions):
            if mask == 0:
                return 1
            res = 0
            for i in range(n):
                if (mask >> i) & 1:
                    new_inversions = inversions
                    for j in range(i):
                        if (mask >> j) & 1:
                            new_inversions -= 1
                    for req in requirements:
                        if i <= req[0] and new_inversions < req[1]:
                            break
                    else:
                        res += dp(mask ^ (1 << i), new_inversions) * comb(n - 1, i)
                        res %= MOD
            return res
        return dp((1 << n) - 1, 0)
```

