from functools import lru_cache
from math import comb
MOD = 10**9 + 7

class Solution:
    def numberOfPermutations(self, n: int, requirements: List[List[int]]) -> int:
        @lru_cache(None)
        def dp(mask, inversions):
            if mask == 0:
                return 1
            res = 0
            for i in range(n):
                if not (mask & (1 << i)):
                    continue
                new_mask = mask ^ (1 << i)
                new_inversions = inversions
                for j in range(i):
                    if mask & (1 << j):
                        new_inversions -= 1
                for req in requirements:
                    if i <= req[0]:
                        new_inversions -= req[1]
                res += dp(new_mask, new_inversions) * comb(n - 1, i)
                res %= MOD
            return res

        return dp((1 << n) - 1, 0)
```

