from typing import List
import math

class Solution:
    def canReachCorner(self, xCorner: int, yCorner: int, circles: List[List[int]]) -> bool:
        def distance(x1, y1, x2, y2):
            return math.sqrt((x1 - x2) ** 2 + (y1 - y2) ** 2)

        def is_valid(x, y):
            for cx, cy, r in circles:
                if distance(x, y, cx, cy) <= r:
                    return False
            return True

        if not is_valid(0, 0) or not is_valid(xCorner, yCorner):
            return False

        if xCorner == 0 or yCorner == 0:
            return True

        dx, dy = xCorner, yCorner
        while dx > 0 and dy > 0:
            if dx > dy:
                dx -= dy
                if not is_valid(dx, 0):
                    return False
            else:
                dy -= dx
                if not is_valid(0, dy):
                    return False

        return True
```

