class Solution:
    def minimumCost(self, m: int, n: int, horizontalCut: List[int], verticalCut: List[int]) -> int:
        MOD = 10**9 + 7
        horizontalCut.sort(reverse=True)
        verticalCut.sort(reverse=True)

        h, v = len(horizontalCut), len(verticalCut)
        hdp, vdp = [0]*(h+1), [0]*(v+1)
        for i in range(1, h+1):
            hdp[i] = hdp[i-1] + horizontalCut[i-1]
        for i in range(1, v+1):
            vdp[i] = vdp[i-1] + verticalCut[i-1]

        res = float('inf')
        for i in range(h+1):
            for j in range(v+1):
                cost = (m - (i if i else m)) * (n - (j if j else n)) + hdp[i] + vdp[j]
                res = min(res, cost)

        return res % MOD
```

```
class Solution:
    def minimumCost(self, m: int, n: int, horizontalCut: List[int], verticalCut: List[int]) -> int:
        MOD = 10**9 + 7
        horizontalCut.sort(reverse=True)
        verticalCut.sort(reverse=True)
        h, v = len(horizontalCut), len(verticalCut)
        dp = [[[0]*(v+1) for _ in range(h+1)] for _ in range(m+1)]

        for i in range(m+1):
            for j in range(h+1):
                dp[i][j][0] = dp[i-1][j][0] + (horizontalCut[j-1] if j > 0 else 0) if i > 0 else 0
                dp[i][j][0] %= MOD

        for i in range(n+1):
            for j in range(v+1):
                dp[0][j][i] = dp[0][j-1][i-1] + (verticalCut[j-1] if j > 0 else 0) if i > 0 else 0
                dp[0][j][i] %= MOD

        for i in range(1, m+1):
            for j in range(1, n+1):
                for k in range(1, h+1):
                    for l in range(1, v+1):
                        dp[i][k][j] = min(dp[i][k][j], dp[i-1][k][j] + (horizontalCut[k-1] if k > 0 else 0), dp[i][k-1][j] + (horizontalCut[k-1] if k > 0 else 0), dp[i][k][j-1] + (verticalCut[l-1] if l > 0 else 0), dp[i][k][j-1] + (verticalCut[l-1] if l > 0 else 0))
                        dp[i][k][j] %= MOD

        return dp[m][h][n]
```

