class Solution:
    def minChanges(self, nums: List[int], k: int) -> int:
        n = len(nums)
        count = [0] * (k + 1)
        for i in range(n // 2):
            diff = abs(nums[i] - nums[n - i - 1])
            count[diff] += 1
        res = n
        for i in range(k + 1):
            res = min(res, n - count[i] - count[i])
        return res
