class Solution:
    def shortestDistanceAfterQueries(self, n: int, queries: List[List[int]]) -> List[int]:
        graph = [[] for _ in range(n)]
        for i in range(n - 1):
            graph[i].append(i + 1)

        for u, v in queries:
            graph[u].append(v)

        dist = [float('inf')] * n
        dist[0] = 0
        for _ in range(n - 1):
            for u in range(n):
                for v in graph[u]:
                    dist[v] = min(dist[v], dist[u] + 1)

        res = []
        for i in range(len(queries)):
            temp_dist = dist.copy()
            for j in range(i + 1):
                u, v = queries[j]
                temp_dist[v] = min(temp_dist[v], temp_dist[u] + 1)
            res.append(temp_dist[-1])

        return res
```

