        dist = [float('inf')] * n
        dist[0] = 0
        pq = [(0, 0)]

        res = []
        for _ in range(len(queries) + 1):
            temp_dist = [float('inf')] * n
            temp_dist[0] = 0
            temp_pq = [(0, 0)]

            while temp_pq:
                d, node = heapq.heappop(temp_pq)
                if d > temp_dist[node]:
                    continue
                for neighbor in graph[node]:
                    if d + 1 < temp_dist[neighbor]:
                        temp_dist[neighbor] = d + 1
                        heapq.heappush(temp_pq, (d + 1, neighbor))

            res.append(temp_dist[-1])
            dist = temp_dist

        return res
