        for k in range(n):
            flip = sum(rows[i] != rows[m - i - 1] for i in range(m // 2)) + sum(cols[j] != cols[n - j - 1] for j in range(n // 2))
            res = min(res, flip)
            for i in range(m):
                rows[i] += grid[i][k] - grid[i][n - k - 1]
            for j in range(n):
                cols[j] += grid[m - k - 1][j] - grid[k][j]
        return res if res != float('inf') else -1
