    def numberOfAlternatingGroups(self, colors: List[int], queries: List[List[int]]) -> List[int]:
        n = len(colors)
        dp = [[0] * 2 for _ in range(n)]
        dp[0][colors[0]] = 1
        for i in range(1, n):
            dp[i][1 - colors[i]] = dp[i - 1][colors[i]] + 1
        res = []
        for q in queries:
            if q[0] == 1:
                size = q[1]
                cnt = 0
                for i in range(n):
                    if dp[i][1 - colors[i]] >= size:
                        cnt += 1
                res.append(cnt)
            else:
                idx, color = q[1], q[2]
                if colors[idx] != color:
                    colors[idx] = color
                    dp = [[0] * 2 for _ in range(n)]
                    dp[0][colors[0]] = 1
                    for i in range(1, n):
                        dp[i][1 - colors[i]] = dp[i - 1][colors[i]] + 1
        return res
