class Solution:
    def numberOfAlternatingGroups(self, colors: List[int], queries: List[List[int]]) -> List[int]:
        n = len(colors)
        colors = colors + colors  # duplicate the array to handle circular case
        dp = [[0] * 2 for _ in range(n * 2)]  # dp[i][j] means the number of alternating groups ending at i with last color j

        dp[0][colors[0]] = 1
        for i in range(1, n * 2):
            for j in range(2):
                if colors[i] == j:
                    dp[i][j] = dp[i - 1][j]
                else:
                    dp[i][j] = dp[i - 1][1 - j] + 1

        res = []
        for query in queries:
            if query[0] == 1:
                size = query[1]
                count = 0
                for i in range(n):
                    count += dp[i + size - 1][1 - colors[i]] - dp[i][1 - colors[i]]
                res.append(count)
            else:
                index, color = query[1], query[2]
                colors[index] = color
                colors[index + n] = color  # update the duplicate array as well

        return res
```

