class Solution:
    def numberOfAlternatingGroups(self, colors: List[int], queries: List[List[int]]) -> List[int]:
        n = len(colors)
        dp = [[0] * (n + 1) for _ in range(2)]
        for i in range(n):
            dp[colors[i]][i + 1] = dp[colors[i]][i] + 1
            dp[1 - colors[i]][i + 1] = dp[1 - colors[i]][i]
        dp2 = [[0] * (n + 1) for _ in range(2)]
        for i in range(n - 1, -1, -1):
            dp2[colors[i]][i] = dp2[colors[i]][i + 1] + 1
            dp2[1 - colors[i]][i] = dp2[1 - colors[i]][i + 1]
        res = []
        for q in queries:
            if q[0] == 1:
                size = q[1]
                cnt = 0
                for i in range(n):
                    if dp[colors[i]][i + size] - dp[colors[i]][i] > 0 and dp2[colors[i]][i] - dp2[colors[i]][i + size] > 0:
                        cnt += 1
                res.append(cnt)
            else:
                idx, color = q[1], q[2]
                colors[idx] = color
                dp[colors[idx]][idx + 1] = dp[colors[idx]][idx] + 1
                dp[1 - colors[idx]][idx + 1] = dp[1 - colors[idx]][idx]
                dp2[colors[idx]][idx] = dp2[colors[idx]][idx + 1] + 1
                dp2[1 - colors[idx]][idx] = dp2[1 - colors[idx]][idx + 1]
        return res
