            heapq.heappush(pq, (-h, -d))

        total_damage = 0
        while pq:
            h, d = 0, 0
            temp = []
            for _ in range(min(len(pq), power)):
                _h, _d = heapq.heappop(pq)
                h -= _h
                d -= _d
                temp.append((_h, _d))
            total_damage += d
            for _h, _d in temp:
                _h += power
                if _h < 0:
