import heapq

class Solution:
    def minDamage(self, power: int, damage: List[int], health: List[int]) -> int:
        pq = []
        for i in range(len(damage)):
            heapq.heappush(pq, (-health[i], -damage[i]))

        total_damage = 0
        while pq:
            temp = []
            for _ in range(min(len(pq), power)):
                h, d = heapq.heappop(pq)
                h += power
                if h < 0:
                    temp.append((h, d))
            total_damage += -sum(d for _, d in pq)
            for item in temp:
                heapq.heappush(pq, item)

        return total_damage
```

