class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        MOD = 10**9 + 7
        n = len(nums)
        dp = [[0]*(51) for _ in range(n+1)]
        dp[0][0] = 1
        for i in range(n):
            for j in range(51):
                for k in range(j, -1, -1):
                    if k <= nums[i]:
                        dp[i+1][j] = (dp[i+1][j] + dp[i][k]) % MOD
        return sum(dp[-1]) % MOD
```

