        for i in range(m):
            for j in range(n):
                if board[i][j] == row_max[i] and board[i][j] == col_max[j]:
                    max_sum += board[i][j]
                    row_max[i] = float('-inf')
                    col_max[j] = float('-inf')
                    if sum(1 for x in row_max if x != float('-inf')) < 3:
                        break
            if sum(1 for x in row_max if x != float('-inf')) < 3:
                break
        return max_sum
