class TrieNode:
    def __init__(self):
        self.children = {}
        self.value = 0

class Trie:
    def __init__(self):
        self.root = TrieNode()

    def insert(self, num):
        node = self.root
        node.value ^= num
        for i in range(31, -1, -1):
            bit = (num >> i) & 1
            if bit not in node.children:
                node.children[bit] = TrieNode()
            node = node.children[bit]
            node.value ^= num

    def query(self, num):
        node = self.root
        for i in range(31, -1, -1):
            bit = (num >> i) & 1
            if 1 - bit in node.children:
                node = node.children[1 - bit]
            else:
                node = node.children[bit]
        return node.value ^ num

class Solution:
    def maximumSubarrayXor(self, nums: List[int], queries: List[List[int]]) -> List[int]:
        trie = Trie()
        prefix_xor = 0
        for num in nums:
            prefix_xor ^= num
            trie.insert(prefix_xor)

        res = []
        for l, r in queries:
            prefix_xor_l = prefix_xor ^ nums[l - 1] if l > 0 else 0
