class Solution:
    def findSafeWalk(self, grid: List[List[int]], health: int) -> bool:
        m, n = len(grid), len(grid[0])
        dp = [[-1] * n for _ in range(m)]

        def dfs(i, j, health):
            if i == m - 1 and j == n - 1:
                return health - grid[i][j] >= 0
            if dp[i][j] != -1:
                return dp[i][j] == 1
            dp[i][j] = 0
            health -= grid[i][j]
            if health < 0:
                return False
            for x, y in [(i - 1, j), (i + 1, j), (i, j - 1), (i, j + 1)]:
                if 0 <= x < m and 0 <= y < n:
                    if dfs(x, y, health):
                        dp[i][j] = 1
                        return True
            return False

        return dfs(0, 0, health)
```

