from collections import deque
from typing import List

class Solution:
    def maxMoves(self, kx: int, ky: int, positions: List[List[int]]) -> int:
        directions = [(2, 1), (1, 2), (-1, 2), (-2, 1), (-2, -1), (-1, -2), (1, -2), (2, -1)]
        memo = {}

        def bfs(x, y, target_x, target_y):
            if (x, y, target_x, target_y) in memo:
                return memo[(x, y, target_x, target_y)]
            queue = deque([(x, y, 0)])
            visited = {(x, y)}
            while queue:
                x, y, step = queue.popleft()
                if (x, y) == (target_x, target_y):
                    memo[(x, y, target_x, target_y)] = step
                    return step
                for dx, dy in directions:
                    nx, ny = x + dx, y + dy
                    if (0 <= nx <= 49) and (0 <= ny <= 49) and (nx, ny) not in visited:
                        queue.append((nx, ny, step + 1))
                        visited.add((nx, ny))
            return float('inf')

        def dfs(positions, is_alice):
            if not positions:
                return 0
            max_moves = float('-inf') if is_alice else float('inf')
            for i, (x, y) in enumerate(positions):
                moves = bfs(kx, ky, x, y)
                new_positions = positions[:i] + positions[i + 1:]
                new_moves = dfs(new_positions, not is_alice)
                if is_alice:
                    max_moves = max(max_moves, moves + new_moves)
                else:
                    max_moves = min(max_moves, moves + new_moves)
            return max_moves

        return dfs(positions, True)
